!function(){"use strict";document.querySelector("[data-openburger]").addEventListener("click",()=>{let e=document.querySelector("[data-burger]");e.classList.add("burger-active"),setTimeout(function(){e.style.opacity="1",e.style.transform="translateX(0)"},100)}),document.querySelector("[data-burgerclose]").addEventListener("click",()=>{let e=document.querySelector("[data-burger]");e.style.opacity="0",e.style.transform="translateX(-200%)",setTimeout(function(){e.classList.remove("burger-active")},100)})}();